const useModal=(initialValue=false)=>{
    const [isOpen,setIsOpen]=useState(initialValue);
    const openModal=()=> setIsOpen(true);
    const closeModal=()=> setIsOpen(false);
    return [isOpen,openModal,closeModal];
}
const Modal=({children,isOpen,closeModal})=>{

    const handleModalContainerClick=(e)=>e.stopPropagation();
    return (
    <article className={`modal ${isOpen && "is-open"}`} onClick={closeModal}>
     <div className="modal-container" onClick={handleModalContainerClick}>
       {children}    
     </div>  
    </article>
    );

}
const Modals=({poke})=>{
    const [isOpenModal1,openModal1,closeModal1]=useModal(false);

    return(
        <div>
            <h3 className="titulo">Habilidades</h3>   
         <Modal isOpen={isOpenModal1} closeModal={closeModal1}>
             <h3>{poke.name}</h3>
             <p>{poke?.abilities?.map(a => <div>{a.ability.name}</div>)}</p>
         </Modal>
        </div>
    );
}
//Fin funsion */
    useEffect(()=>{
    console.log({pokemon});
    }, [pokemon,counter]);

    return ( 
    <div className = "App" >
         <header className = "App-header">
        <div className='flex-container'>
        <img src = { pokemon?.sprites?.front_default } className = "poke-image" alt = "logo"/>
        <img src = { pokemon?.sprites?.back_default } className = "poke-image" alt = "logo"/>
        </div>
        <p>
            Pokemon id: {pokemon.id??"NO POKEMON SELECTED "}<br/>
            Nombre del pokemon: {pokemon.name ?? "NO POKEMON SELECTED"}</p>

        <div className='flex-container'>
        <button className='button' onClick={getBack}>Back</button>
        <button className='button' onClick={() => fetchPokemon(getRandomInt())}>Random</button>
        <button className='button' onClick={getNext}>Next</button>
        </div>
        {counter>0 ?  <Modals poke={pokemon}/>:<h1 className="none">.</h1>}


        <p></p>
        <p></p> 

       </header> 
    </div>
    );
}

export default App;
