import logo from './logo.svg';
import './App.css';
import {useEffect, useState } from "react";

function App() {
    const [pokemon, setPokemon] = useState({});

    const fetchPokemon = (id)=>{
        fetch(`https://pokeapi.co/api/v2/pokemon/${id}`)
        .then((response)=> response.json())
        .then((data)=>setPokemon(data));

    };

    const getRandomInt = (min = 1, max = 500)=>{
        return Math.floor(Math.random() * (max - min) + min);
    };
    const getNext =(next) =>{
        return pokemon +1;
    };

    useEffect(()=>{
    console.log({pokemon});
    }, [pokemon]);

    return ( 
    <div className = "App" >
         <header className = "App-header">
        <div className='flex-container'>
        <img src = { pokemon?.sprites?.back_default } className = "poke-image" alt = "logo"/>
        <img src = { pokemon?.sprites?.front_default } className = "poke-image" alt = "logo"/>
        </div>
        <p>
            Pokemon id: {pokemon.id??"NO POKEMON SELECTED "}<br/>
            Nombre del pokemon: {pokemon.name ?? "NO POKEMON SELECTED"}</p><br />
            {pokemon.abilities??"NO POKEMON SELECTED"}
        <div className='flex-container'>
        <button className='button'>Back</button>
        <button className='button' onClick={() => fetchPokemon(getRandomInt())}>Random</button>
        <button className='button' onClick={()=> fetchPokemon(getNext())}>Next</button>
        <button className='button'>Habilidades</button>
        </div>


        <p></p>
        <p></p> 

       </header> 
    </div>
    );
}
export default App;
